svg.script:
var step = 0;
var anim = false;
document.querySelectorAll('#input1 > animateMotion, #input2 > animateMotion, #input3 > animateMotion, #input4 > animateMotion, #input5 > animateMotion').forEach(function(e) {
  e.onbegin = ev => e.parentElement.style.opacity = step > 0 ? 1 : 0;
  e.onend = ev => {
    e.parentElement.style.opacity = 0;
    if (!anim) return;
    if (step === 2) {
      document.querySelectorAll('#myelin1, #myelin2, #myelin3, #myelin4, #myelin5, #axon_terminal').forEach(myelin => {
        myelin.classList.remove('blink');
        myelin.clientHeight;
        myelin.classList.add('blink');
      });
    }
    anim = false;
  };
  e.endElement();
});
function next(event) {
  if (event.data === 'next') step++;
  else if (event.data === 'prev') step--;

  if (step === 0) {
    document.getElementById('first_time').style.opacity = 0;
    document.getElementById('second_time').style.opacity = 0;
  }
  else if (step === 1) {
    document.getElementById('first_time').style.opacity = 1;
    document.getElementById('second_time').style.opacity = 0;
    document.querySelectorAll('#input3 > animateMotion').forEach(e => e.beginElement());
    anim = true;
  }
  else if (step === 2) {
    document.getElementById('first_time').style.opacity = 0;
    document.getElementById('second_time').style.opacity = 1;
    document.querySelectorAll('#input1 > animateMotion, #input2 > animateMotion, #input3 > animateMotion, #input4 > animateMotion, #input5 > animateMotion').forEach(e => e.beginElement());
    anim = true;
  }
}
window.addEventListener('message', next, false);
